# =============================================================================
# MAPEAMENTO DE ATRIBUTOS - AN√ÅLISE DO ARQUIVO QMD COMPLETO
# =============================================================================

# Lista completa de atributos conhecidos do Tesouro Gerencial
atributos_tg &lt;- c(
  # Organizacionais
  "orgao_uge_orcam_fiscal_s_n", "uo_orgao_maximo_codigo", "uo_poder",
  "unidade_orcamentaria_codigo", "unidade_gestora_codigo",
  "orgao_codigo", "uo_orgao_maximo_nome",

  # Despesa
  "grupo_despesa_codigo_grupo", "elemento_despesa_codigo",
  "natureza_despesa_detalhada_codigo", "natureza_despesa_detalhada_nome",
  "modalidade_aplicacao_codigo", "categoria_economica_codigo",
  "natureza_despesa_codigo",

  # Funcional-program√°tica
  "funcao_governo_codigo", "subfuncao_governo_codigo",
  "programa_governo_codigo", "acao_governo_codigo",
  "plano_orcamentario_codigo_po",

  # Fonte e identificadores
  "fonte_recursos_codigo", "iduso_codigo",
  "resultado_primario_codigo", "resultado_eof_codigo",

  # Receita
  "natureza_receita_codigo", "natureza_receita_codigo_completo",
  "nre1_categoria_economica_codigo", "nre2_origem_codigo",

  # Cont√°bil
  "item_informacao_codigo", "item_informacao_nome",
  "conta_contabil_codigo", "entidade_codigo", 
  "tipo_administracao_codigo", "tipo_entidade_codigo",

  # Temporal
  "mes_lancamento", "ano_inscricao", "data_lancamento",

  # Derivados
  "tipo_modalidade", "refinanciamento"
)

# Ler o arquivo QMD
arquivo_qmd &lt;- "relatorios_fiscais_inova.qmd"
conteudo_qmd &lt;- readLines(arquivo_qmd, encoding = "UTF-8", warn = FALSE)

# ============================================================================
# IDENTIFICAR CHUNKS E SEUS CONTEXTOS (DEMONSTRATIVO/ANEXO)
# ============================================================================

chunk_inicio &lt;- grep("^```\\{r", conteudo_qmd)
chunk_fim &lt;- grep("^```$", conteudo_qmd)

extrair_label_chunk &lt;- function(linha) {
  match &lt;- str_match(linha, "```\\{r\\s+([^,}\\s]+)")
  if (!is.na(match[1, 2])) return(match[1, 2])
  return(NA_character_)
}

inferir_demonstrativo_anexo &lt;- function(label, linha_num, conteudo) {

  if (is.na(label)) label &lt;- ""
  label_lower &lt;- tolower(label)

 # RGF
  if (str_detect(label_lower, "rgf.?a01|rgf.?01|pessoal|classificar-pessoal")) {
    return(list(demonstrativo = "RGF", anexo = "A01"))
  }
  if (str_detect(label_lower, "rgf.?a02|rgf.?02|dcl|divida")) {
    return(list(demonstrativo = "RGF", anexo = "A02"))
  }
  if (str_detect(label_lower, "rgf.?a03|rgf.?03|garantia")) {
    return(list(demonstrativo = "RGF", anexo = "A03"))
  }
  if (str_detect(label_lower, "rgf.?a04|rgf.?04|operacoes.?credito")) {
    return(list(demonstrativo = "RGF", anexo = "A04"))
  }
  if (str_detect(label_lower, "rgf.?a05|rgf.?05|disponibilidade")) {
    return(list(demonstrativo = "RGF", anexo = "A05"))
  }

  # RREO
  if (str_detect(label_lower, "rreo.?a01|rreo.?01|balanco.?orcam")) {
    return(list(demonstrativo = "RREO", anexo = "A01"))
  }
  if (str_detect(label_lower, "rreo.?a03|rreo.?03|rcl")) {
    return(list(demonstrativo = "RREO", anexo = "A03"))
  }
  if (str_detect(label_lower, "rreo.?a04|rreo.?04|rgps|rpps|previdencia")) {
    return(list(demonstrativo = "RREO", anexo = "A04"))
  }
  if (str_detect(label_lower, "rreo.?a06|rreo.?06|resultado.?primario|primario")) {
    return(list(demonstrativo = "RREO", anexo = "A06"))
  }
  if (str_detect(label_lower, "rreo.?a07|rreo.?07|rp.?07")) {
    return(list(demonstrativo = "RREO", anexo = "A07"))
  }
  if (str_detect(label_lower, "rreo.?a08|rreo.?08|mde|educacao")) {
    return(list(demonstrativo = "RREO", anexo = "A08"))
  }
  if (str_detect(label_lower, "rreo.?a12|rreo.?12|rp.?12")) {
    return(list(demonstrativo = "RREO", anexo = "A12"))
  }
  if (str_detect(label_lower, "rreo.?a14|rreo.?14")) {
    return(list(demonstrativo = "RREO", anexo = "A14"))
  }
  if (str_detect(label_lower, "t01|seguridade")) {
    return(list(demonstrativo = "RREO", anexo = "T01"))
  }
  if (str_detect(label_lower, "t02")) {
    return(list(demonstrativo = "RREO", anexo = "T02"))
  }

  # Buscar contexto nas linhas anteriores
  for (i in max(1, linha_num - 50):linha_num) {
    linha &lt;- conteudo[i]
    if (str_detect(linha, regex("^#+\\s+.*RGF.*Anexo\\s*(\\d+)", ignore_case = TRUE))) {
      anexo_num &lt;- str_extract(linha, "\\d+")
      return(list(demonstrativo = "RGF", anexo = paste0("A", str_pad(anexo_num, 2, pad = "0"))))
    }
    if (str_detect(linha, regex("^#+\\s+.*RREO.*Anexo\\s*(\\d+)", ignore_case = TRUE))) {
      anexo_num &lt;- str_extract(linha, "\\d+")
      return(list(demonstrativo = "RREO", anexo = paste0("A", str_pad(anexo_num, 2, pad = "0"))))
    }
  }

  return(list(demonstrativo = "GERAL", anexo = "GERAL"))
}

# ============================================================================
# FUN√á√ÉO PARA CLASSIFICAR CONTEXTO (SIMPLIFICADO)
# ============================================================================
# Categorias:
#   - criterio: dentro de lista criterios_*
#   - transformacao: mutate, case_when, classifica√ß√µes
#   - filtro: filter, condi√ß√µes de sele√ß√£o
#   - agrupamento: group_by, count, summarise

classificar_contexto &lt;- function(texto_linha, linhas_anteriores = NULL) {

  # 1. Crit√©rio: linha cont√©m "criterio ="
  if (str_detect(texto_linha, "criterio\\s*=")) {
    return("criterio")
  }

  # 2. Agrupamento: group_by, count, summarise
  if (str_detect(texto_linha, "group_by|count\\s*\\(|summarise|summarize")) {
    return("agrupamento")
  }

  # 3. Filtro: filter()
  if (str_detect(texto_linha, "filter\\s*\\(")) {
    return("filtro")
  }

  # 4. Transforma√ß√£o: mutate, case_when, ou linhas com ~ (dentro de case_when)
  if (str_detect(texto_linha, "mutate\\s*\\(|case_when|~")) {
    return("transformacao")
  }

  # 5. Condi√ß√µes soltas (==, %in%, etc) - provavelmente parte de transforma√ß√£o
  if (str_detect(texto_linha, "%in%|==|!=|&gt;=|&lt;=|&gt;|&lt;")) {
    return("transformacao")
  }

  # 6. Resto
  return("outro")
}

# ============================================================================
# MAPEAR ATRIBUTOS POR CHUNK
# ============================================================================

resultados_chunks &lt;- tibble(
  demonstrativo = character(),
  anexo = character(),
  atributo = character(),
  contexto = character(),
  linha = integer()
)

for (i in seq_along(chunk_inicio)) {
  inicio &lt;- chunk_inicio[i]

  fim_idx &lt;- which(chunk_fim &gt; inicio)[1]
  if (is.na(fim_idx)) next
  fim &lt;- chunk_fim[fim_idx]

  label &lt;- extrair_label_chunk(conteudo_qmd[inicio])
  info &lt;- inferir_demonstrativo_anexo(label, inicio, conteudo_qmd)

  # Ignorar chunks gen√©ricos
  if (info$demonstrativo == "GERAL" && !is.na(label)) {
    if (str_detect(label, "setup|import|funcao|dados_|config")) next
  }

  conteudo_chunk &lt;- conteudo_qmd[inicio:fim]

  for (atributo in atributos_tg) {
    pattern &lt;- paste0("\\b", atributo, "\\b")
    linhas_match &lt;- grep(pattern, conteudo_chunk)

    if (length(linhas_match) &gt; 0) {
      for (linha_rel in linhas_match) {
        linha_abs &lt;- inicio + linha_rel - 1
        texto_linha &lt;- conteudo_chunk[linha_rel]

        # Ignorar coment√°rios puros
        if (str_detect(texto_linha, "^\\s*#[^|]") && !str_detect(texto_linha, "criterio")) next

        contexto &lt;- classificar_contexto(texto_linha)

        resultados_chunks &lt;- bind_rows(resultados_chunks, tibble(
          demonstrativo = info$demonstrativo,
          anexo = info$anexo,
          atributo = atributo,
          contexto = contexto,
          linha = linha_abs
        ))
      }
    }
  }
}

# Remover duplicatas e agregar
atributos_mapeados_codigo &lt;- resultados_chunks %&gt;%
  filter(demonstrativo != "GERAL") %&gt;%
  distinct(demonstrativo, anexo, atributo, contexto) %&gt;%
  arrange(demonstrativo, anexo, atributo)

# Criar resumo geral
if (nrow(atributos_mapeados_codigo) &gt; 0) {

  resumo_atributos_codigo &lt;- atributos_mapeados_codigo %&gt;%
    group_by(atributo) %&gt;%
    summarise(
      qtd_usos = n(),
      demonstrativos = paste(unique(demonstrativo), collapse = ", "),
      anexos = paste(sort(unique(paste0(demonstrativo, " ", anexo))), collapse = " | "),
      contextos = paste(unique(contexto), collapse = ", "),
      .groups = "drop"
    ) %&gt;%
    arrange(desc(qtd_usos))

} else {
  resumo_atributos_codigo &lt;- tibble(
    atributo = character(),
    qtd_usos = integer(),
    demonstrativos = character(),
    anexos = character(),
    contextos = character()
  )
}



